
import express from 'express';
import pkg from 'pg';
const { Pool } = pkg;
const router = express.Router();

// Lazy pool creation to avoid blocking on import (prevents cold-start timeouts)
let pool;
function getPool() {
  if (!pool && process.env.DATABASE_URL) {
    pool = new Pool({
      connectionString: process.env.DATABASE_URL,
      connectionTimeoutMillis: 10000, // 10s to fail fast
      idleTimeoutMillis: 10000,
      max: 5,
    });
  }
  return pool;
}

// VERY SIMPLE: category only
router.get('/', async (req, res) => {
  const { categoryId } = req.query;

  if (!categoryId) {
    return res.json([]);
  }

  try {
    const dbPool = getPool();
    if (!dbPool) {
      return res.status(503).json({ error: 'Database not configured' });
    }

    const result = await dbPool.query(
      `SELECT id, title, price
       FROM "Products"
       WHERE "CategoryId" = $1
       ORDER BY id`,
      [categoryId]
    );

    res.json(result.rows);
  } catch (err) {
    console.error('Products(min) error:', err.message || err);
    // If connection error, return 503 so frontend can show retry
    if (err.code === 'ENOTFOUND' || err.message?.includes('ENOTFOUND')) {
      return res.status(503).json({ error: 'Database connection failed' });
    }
    res.status(500).json({ error: 'Failed to load products' });
  }
});

export default router;
